package day4_operators;

public class PrintFormatter {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		// Printf - can use %s, %d, %f, %b, %.2f
		System.out.printf("Hello My name is %s", "Jessica"); // any word after comma will replace %s
	System.out.println(); // added for a new line
		System.out.printf("%s My name is %s","Hello", "Jessica"); // 
		System.out.println();
		System.out.printf("I am %d, my name is %s", 31, "Jessica");
		System.out.println();
		System.out.printf("I am %d, you are %d. My name is %s", 37, 32, "Jessica");
		System.out.println();
		System.out.printf("Is sunny today? %b", false);
		
		
		
		
	}

}
