package week3_review;

public class PrintAlphabetsWithTheirASCIIValue {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		
		char A = 'A';
		while (A <= 'z') {
			int asciiValue = A;
			System.out.println(A+" "+asciiValue);
			A++;
		}
		
		
		
		
		
		
		
		
		
	}

}
